"use strict";(self.webpackChunkmydatahack_blog_site=self.webpackChunkmydatahack_blog_site||[]).push([[9439],{48373:(e,n,t)=>{t.r(n),t.d(n,{assets:()=>i,contentTitle:()=>r,default:()=>m,frontMatter:()=>s,metadata:()=>c,toc:()=>a});var l=t(74848),o=t(28453);const s={sidebar_position:37},r="Unit Testing Event Listeners on Form Input Fields with Karma",c={id:"Web/Frontend/event-listener-test-karma",title:"Unit Testing Event Listeners on Form Input Fields with Karma",description:"often associated with Angular, but it is a really nice test runner for regular front-end JS code as well. It works well with CD/CI pipelines as it spawns a headless chrome for test execution environment.",source:"@site/docs/Web/Frontend/37.event-listener-test-karma.md",sourceDirName:"Web/Frontend",slug:"/Web/Frontend/event-listener-test-karma",permalink:"/docs/Web/Frontend/event-listener-test-karma",draft:!1,unlisted:!1,tags:[],version:"current",sidebarPosition:37,frontMatter:{sidebar_position:37},sidebar:"tutorialSidebar",previous:{title:"Replacing Local Storage with IndexedDB",permalink:"/docs/Web/Frontend/indexdb"},next:{title:"Creating a Form for Screen Reader Accessibility",permalink:"/docs/Web/Frontend/accessible-from"}},i={},a=[];function d(e){const n={code:"code",h1:"h1",p:"p",pre:"pre",...(0,o.R)(),...e.components};return(0,l.jsxs)(l.Fragment,{children:[(0,l.jsx)(n.h1,{id:"unit-testing-event-listeners-on-form-input-fields-with-karma",children:"Unit Testing Event Listeners on Form Input Fields with Karma"}),"\n",(0,l.jsx)(n.p,{children:"often associated with Angular, but it is a really nice test runner for regular front-end JS code as well. It works well with CD/CI pipelines as it spawns a headless chrome for test execution environment."}),"\n",(0,l.jsx)(n.p,{children:"Karma works well with TypeScript. Setting up is not too difficult. It simply uses a webpack to transpile TS into JS and runs the test. Once it is set up, it works exactly the same as JS. You can check my example of karma.config.js for TypeScript. Dependencies to install can be found here."}),"\n",(0,l.jsx)(n.p,{children:"The focus on this post is to write unit tests for the event listeners attached to select, radio and checkbox input. Because Karma runs on the actual browser, we do not need to learn special APIs (like you need to do with Jest & Enzyme for React) to do browser actions. We can just use the vanilla JavaScript to programmatically update input fields and dispatch actions."}),"\n",(0,l.jsx)(n.p,{children:"Let\u2019s get started."}),"\n",(0,l.jsx)(n.p,{children:"(1) Select Input Event Listener Tests"}),"\n",(0,l.jsx)(n.p,{children:"We have a select element event listener that adds different classes."}),"\n",(0,l.jsx)(n.pre,{children:(0,l.jsx)(n.code,{className:"language-ts",children:'import { IEventHandler } from "./types/interfaces";\n\nclass SelectEventHandler implements IEventHandler {\n  private formElement: HTMLElement;\n  private selectElements: any;\n\n  constructor(formElement: HTMLElement) {\n    this.formElement = formElement;\n    this.selectElements = this.formElement.querySelectorAll("select");\n  }\n\n  public init(): void {\n    this.selectStyleAddEvents();\n  }\n\n  private selectStyleAddEvents(): void {\n    for (let elem of this.selectElements) {\n      elem.addEventListener("input", (e: any) => {\n        this.changeStyle(e.target.value, elem);\n      });\n      elem.addEventListener("blur", (e: any) => {\n        this.changeStyle(e.target.value, elem);\n      });\n      elem.addEventListener("focus", () => {\n        elem.style.color = "black";\n      });\n    }\n  }\n\n  private changeStyle(value: string, elem: HTMLElement): void {\n    if (value.length > 0) {\n      elem.style.color = "black";\n    } else {\n      elem.style.color = "rgb(117, 117, 117)";\n    }\n  }\n}\n\nexport default SelectEventHandler;\n'})}),"\n",(0,l.jsx)(n.p,{children:"Let\u2019s unit test this. The most important thing about select element is that the value must exist in the child option elements. If we put non-existing value, the value will not change."}),"\n",(0,l.jsx)(n.pre,{children:(0,l.jsx)(n.code,{className:"language-ts",children:'import SelectEventHandler from "../SelectEventHandler";\n\ndescribe("SelectEventHandler.spec.ts", () => {\n  beforeEach(() => {\n    const fixture = `<div id="fixture">\n      <form id="mock-form">\n        <select id="first-select">\n          <option value="" class="default">Select option</option>\n          <option value="Dr" class="option">Dr</option>\n        </select>\n        <select id="second-select">\n          <option value="" class="default">Select option</option>\n          <option value="Dr" class="option">Dr</option>\n        </select>\n      </form>\n    </div>`;\n\n    document.body.insertAdjacentHTML("afterbegin", fixture);\n  });\n\n  afterEach(() => {\n    document.body.removeChild(\n      document.getElementById("fixture") as HTMLElement\n    );\n  });\n\n  it("should change to ligher color on all select elements on input without any input value", () => {\n    const formElement = document.querySelector("#mock-form") as HTMLElement;\n    const selectEventHandler = new SelectEventHandler(formElement);\n    selectEventHandler.init();\n    const selectElements = document.querySelectorAll("select") as any;\n\n    for (let selectElem of selectElements) {\n      selectElem.dispatchEvent(new Event("input"));\n      expect(selectElem.style.color).toBe("rgb(117, 117, 117)");\n    }\n  });\n\n  it("should change to black on all select elements on input with input value", () => {\n    const formElement = document.querySelector("#mock-form") as HTMLElement;\n    const selectEventHandler = new SelectEventHandler(formElement);\n    selectEventHandler.init();\n    const selectElements = document.querySelectorAll("select") as any;\n\n    for (let selectElem of selectElements) {\n      // this value needs to be the one exists in option.\n      // if you put random value like \'hello\', this will not work.\n      selectElem.value = "Dr";\n      selectElem.dispatchEvent(new Event("input"));\n      expect(selectElem.style.color).toBe("black");\n    }\n  });\n\n  it("should change color to black on all select elements on focus", () => {\n    const formElement = document.querySelector("#mock-form") as HTMLElement;\n    const selectEventHandler = new SelectEventHandler(formElement);\n    selectEventHandler.init();\n    const selectElements = document.querySelectorAll("select") as any;\n\n    for (let selectElem of selectElements) {\n      selectElem.dispatchEvent(new Event("focus"));\n      expect(selectElem.style.color).toBe("black");\n    }\n  });\n\n  it("should change to ligher color on all select elements on blur without any input value", () => {\n    const formElement = document.querySelector("#mock-form") as HTMLElement;\n    const selectEventHandler = new SelectEventHandler(formElement);\n    selectEventHandler.init();\n    const selectElements = document.querySelectorAll("select") as any;\n\n    for (let selectElem of selectElements) {\n      selectElem.dispatchEvent(new Event("blur"));\n      expect(selectElem.style.color).toBe("rgb(117, 117, 117)");\n    }\n  });\n\n  it("should change to black on all select elements on blur with input value", () => {\n    const formElement = document.querySelector("#mock-form") as HTMLElement;\n    const selectEventHandler = new SelectEventHandler(formElement);\n    selectEventHandler.init();\n    const selectElements = document.querySelectorAll("select") as any;\n\n    for (let selectElem of selectElements) {\n      selectElem.value = "Dr";\n      selectElem.dispatchEvent(new Event("blur"));\n      expect(selectElem.style.color).toBe("black");\n    }\n  });\n});\n'})}),"\n",(0,l.jsx)(n.p,{children:"(2) Radio & Checkbox Input Event Listener Tests"}),"\n",(0,l.jsx)(n.p,{children:"Here is the simple event listener class to listen to the change on radio & checkbox input and update aria-checked attribute on click."}),"\n",(0,l.jsx)(n.pre,{children:(0,l.jsx)(n.code,{className:"language-ts",children:'class FormEventHandler implements IEventHandler {\n  private formElement: HTMLElement;\n\n  constructor(formElement: HTMLElement) {\n    this.formElement = formElement;\n  }\n\n  public init(): void {\n    this.radioInputHandler();\n    this.checkboxInputHandler();\n  }\n\n  private radioInputHandler(): void {\n    const radioInputElements =\n      this.formElement.querySelectorAll(".radio-input-ac");\n    this.addEventListenerForAriaCheckedRadio(radioInputElements);\n  }\n\n  private checkboxInputHandler(): void {\n    const checkboxInputElements =\n      this.formElement.querySelectorAll(".checkbox-input-ac");\n    this.addEventListenerForAriaCheckedCheckBox(checkboxInputElements);\n  }\n\n  private addEventListenerForAriaCheckedRadio(elementList: any): void {\n    for (let elem of elementList) {\n      elem.addEventListener("click", () => {\n        for (let elem of elementList) {\n          if (elem.checked) {\n            elem.setAttribute("aria-checked", "true");\n          } else {\n            elem.setAttribute("aria-checked", "false");\n          }\n        }\n      });\n    }\n  }\n\n  private addEventListenerForAriaCheckedCheckBox(elementList: any): void {\n    for (let elem of elementList) {\n      elem.addEventListener("click", (e: Event) => {\n        this.setAriaChecked(<HTMLInputElement>e.target);\n      });\n    }\n  }\n\n  private setAriaChecked(elem: HTMLInputElement): void {\n    if (elem.checked) {\n      elem.setAttribute("aria-checked", "true");\n    } else {\n      elem.setAttribute("aria-checked", "false");\n    }\n  }\n}\n\nexport default FormEventHandler;\n'})}),"\n",(0,l.jsx)(n.p,{children:"We can unit test the public method where it attaches event listeners to elements. The testing is fairly simple, updating value and dispatching action."}),"\n",(0,l.jsx)(n.pre,{children:(0,l.jsx)(n.code,{className:"language-ts",children:'import FormEventHandler from "../FormEventHandler";\n\ndescribe("FormEventHandler.spec.ts", () => {\n  beforeEach(() => {\n    const fixture = `<form id="fixture">\n      <div role="radiogroup">\n        <h3>Radio buttons</h3>\n        <div role="radio" class="radio-input-ac" \n          value="option1" id="option1">\n          Option 1\n        </div>\n        <div role="radio" class="radio-input-ac" \n          value="option2" id="option2">\n          Option 2\n        </div>\n        <div role="radio" class="radio-input-ac" \n          value="option3" id="option3">\n          Option 3\n        </div>\n      </div>\n      <div class="select-section">\n        <span role="checkbox" aria-checked="false" \n          class="checkbox-input-ac"\n          id="subscribe" name="subscribe" /></span>\n        <label for="subscribe">subscribe</label>\n      </div>\n    </form>`;\n\n    document.body.insertAdjacentHTML("afterbegin", fixture);\n  });\n\n  afterEach(() => {\n    document.body.removeChild(\n      document.getElementById("fixture") as HTMLElement\n    );\n  });\n\n  it("should set correct aria-checked attritube on radio input", () => {\n    // Arrange\n    const formElement = document.querySelector("#fixture") as HTMLElement;\n    const formEventHandler = new FormEventHandler(formElement);\n\n    // Act\n    formEventHandler.init();\n    const clickedElement = document.getElementById(\n      "option1"\n    ) as HTMLInputElement;\n    clickedElement.checked = true;\n    clickedElement.click();\n\n    // Assert\n    const radioGroupElements = document.querySelectorAll(\n      ".radio-input-ac"\n    ) as any;\n    for (let elem of radioGroupElements) {\n      if (elem.getAttribute("id") === "option1") {\n        expect(elem.getAttribute("aria-checked")).toBe("true");\n      } else {\n        expect(elem.getAttribute("aria-checked")).toBe("false");\n      }\n    }\n  });\n\n  it("should set aria-checked to true on checkbox input when ticked", () => {\n    // Arrange\n    const formElement = document.querySelector("#fixture") as HTMLElement;\n    const formEventHandler = new FormEventHandler(formElement);\n\n    // Act\n    formEventHandler.init();\n    const clickedElement = document.getElementById(\n      "subscribe"\n    ) as HTMLInputElement;\n    clickedElement.checked = true;\n    clickedElement.click();\n\n    // Assert\n    expect(clickedElement.getAttribute("aria-checked")).toBe("true");\n  });\n\n  it("should set aria-checked to false on checkbox input when not ticked", () => {\n    // Arrange\n    const formElement = document.querySelector("#fixture") as HTMLElement;\n    const formEventHandler = new FormEventHandler(formElement);\n\n    // Act\n    formEventHandler.init();\n    const clickedElement = document.getElementById(\n      "subscribe"\n    ) as HTMLInputElement;\n    clickedElement.click();\n\n    // Assert\n    expect(clickedElement.getAttribute("aria-checked")).toBe("false");\n  });\n});\n'})}),"\n",(0,l.jsx)(n.p,{children:"You can see more comprehensive Karma unit test example here. Karma uses Jasmine as a testing framework out of the box. To write unit tests, cheatsheet is always your best friend!"}),"\n",(0,l.jsx)(n.p,{children:"(2019-10-13)"})]})}function m(e={}){const{wrapper:n}={...(0,o.R)(),...e.components};return n?(0,l.jsx)(n,{...e,children:(0,l.jsx)(d,{...e})}):d(e)}},28453:(e,n,t)=>{t.d(n,{R:()=>r,x:()=>c});var l=t(96540);const o={},s=l.createContext(o);function r(e){const n=l.useContext(s);return l.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function c(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(o):e.components||o:r(e.components),l.createElement(s.Provider,{value:n},e.children)}}}]);